<div class = "row">    <div class = "col-xs-12 col-md-8">        <h1 class = "page-header">Horario</h1>    </div></div><div class="form-group col-sm-6 col-lg-4">    {!! Form::label('dia', 'Día:') !!}    {!! Form::select('dia', [        '0'=>'Domingo',        '1'=>'Lunes',        '2'=>'Martes',        '3'=>'Miércoles',        '4'=>'Jueves',        '5'=>'Viernes',        '6'=>'Sábado',        ], null, ['class' => 'form-control']) !!}</div><div class="form-group col-sm-6 col-lg-4">    {!! Form::label('tipo', 'Tipo:') !!}    {!! Form::select('tipo', [        '1'=>'Consulta',        '2'=>'Cirugía'        ], null, ['class' => 'form-control']) !!}</div><fieldset class="grupo col-xs-12 col-lg-8" style="z-index: 2;">    <h4>Rango de Horas:</h4>    <div class="form-group col-sm-6">        {!! Form::label('horaDe', 'De:') !!}        {!! Form::text('horaDe', null, ['class' => 'form-control','autocomplete' => 'off', 'size' => '10']) !!}    </div>    <div class="form-group col-sm-6">        {!! Form::label('horaA', 'A:') !!}        {!! Form::text('horaA', null, ['class' => 'form-control','autocomplete' => 'off', 'size' => '10']) !!}    </div></fieldset><div class="form-group col-sm-12" style="z-index: 1;">    {{--{!! Form::button('Guardar <i class = "glyphicon glyphicon-floppy-save"></i>', ['class' => 'btn btn-success',        'type'=>'submit']) !!}--}}    {!! Form::button('Guardar <i class = "glyphicon glyphicon-floppy-save"></i>', ['class' => 'btn btn-success',        'onclick'=>'pulsar(this)']) !!}        <a class = "btn btn-danger" href = "{!! route('horarios.index') !!}">            Cancelar            <i class = "glyphicon glyphicon-floppy-remove"></i>        </a></div><script type="text/javascript">    jQuery(function() {        $("#horaDe, #horaA").timePicker({            startTime: "08:00",  //Indica el inicio            endTime: new Date(0, 0, 0, 21, 30, 0),  //Indica que la fecha de fin es a las 15:30            show24Hours: true, //Formato de fechas AM y PM            separator:':', //Separador de horas y minutos            step: 30 //Frecuencia de cada intervalo        });    });    $(document).on('ready',function() {        $('#horaA').on('change', function(){            var horaDe = $("#horaDe").val();            var horaA = $(this).val();            /*console.log(horaDe +" > "+ horaA);*/            if (horaDe >= horaA){                swal("Espere","La Hora de inicio es mayor o igual a la final","warning");            }        });    });    function pulsar(este){        var dia = $("#dia").val();        var horaDe = $("#horaDe").val();        var horaA = $("#horaA").val();        $.ajax({            async: false,            type: 'GET',            url: '/horarios/horarios/todos',            success: function (data) {                console.log(data);                for (var i = 0; i < data.length; i++) {                    if (dia == data[i].dia){                        /*console.log(horaDe +" >= "+ data[i].horaDe.slice(0, -3));                        console.log(horaDe +" <= "+ data[i].horaA.slice(0, -3));*/                        if ((horaDe > data[i].horaDe.slice(0, -3)) && (horaDe < data[i].horaA.slice(0, -3))) {                            return swal("Espere", "Este día, en este rango de horas ya está dado de alta.", "warning");                        }else if ((horaA > data[i].horaDe.slice(0, -3)) && (horaA < data[i].horaA.slice(0, -3))) {                            return swal("Espere", "Este día, en este rango de horas ya está dado de alta.", "warning");                        }                        if ((data[i].horaDe.slice(0, -3) > horaDe) && (data[i].horaDe.slice(0, -3) < horaA)) {                            return swal("Espere", "Este día, en este rango de horas ya está dado de alta.", "warning");                        }else if ((data[i].horaA.slice(0, -3) > horaDe) && (data[i].horaA.slice(0, -3) < horaA)) {                            return swal("Espere", "Este día, en este rango de horas ya está dado de alta.", "warning");                        }                    }                }                $(este.form).submit();            }, error: function (ajaxContext) {                swal("Espere", "Algo salió mal, reintente de nuevo o comisarse con su administrador.", "warning");            }        });    }</script>