<div class = "row">    <div class = "col-xs-12 col-md-8">        <h1 class = "page-header">Material</h1>    </div></div><div class="form-group col-sm-6 col-lg-4">    {!! Form::label('nom_prod', 'Nombre de Material:') !!}    {!! Form::text('nom_prod', null, ['class' => 'form-control', 'maxlength' => 60]) !!}</div><!--- Precio Unitario Field ---><div class="form-group col-sm-6 col-lg-4">    {!! Form::label('precio_unitario2', 'Precio Unitario:') !!}    {!! Form::text('precio_unitario2', null, ['class' => 'form-control currency', 'placeholder'=>'$', 'onkeyup' => 'servicio(this.form)']) !!}</div>{!! Form::hidden('precio_unitario', null, array('id' => 'precio_unitario')) !!}<script>    function servicio(form) {        $('#precio_unitario2').priceFormat({            prefix: '$ ',            centsSeparator: '.',            thousandsSeparator: ',',            allowNegative: false        });        var precio = $('#precio_unitario2').unmask();        precio = precio / 100;        $('#precio_unitario').val(precio);        return;    }</script><div class="form-group col-sm-6 col-lg-4">    {!! Form::label('descripcion_prod', 'DescripciÃ³n:') !!}    {!! Form::textarea('descripcion_prod', null, ['class' => 'form-control', 'rows' => 5, 'maxlength' => 225]) !!}</div><!--- Submit Field ---><div class="form-group col-sm-12">    {!! Form::button('Guardar <i class = "glyphicon glyphicon-floppy-save"></i>', ['class' => 'btn btn-success',        'type'=>'submit']) !!}        <a class = "btn btn-danger" href = "{!! route('productos.index') !!}">            Cancelar            <i class = "glyphicon glyphicon-floppy-remove"></i>        </a></div>